<script>
document.addEventListener("DOMContentLoaded", function() {
    // Revenue by Category Chart
    var revChart = new ApexCharts(document.querySelector("#revenueByCategoryChart"), {
        chart: { type: 'bar', height: 320, toolbar: { show: false } },
        series: [{ name: 'Revenue ()', data: [42000, 36000, 27500, 49000] }],
        xaxis: { categories: ['Car', 'Van', 'Bus', 'SUV'] },
        colors: ['#4e73df'],
        plotOptions: { bar: { borderRadius: 6, columnWidth: '45%' } },
        dataLabels: { enabled: false },
        grid: { borderColor: '#f1f1f1' },
        tooltip: { y: { formatter: val => '' + val.toLocaleString() } }
    });
    revChart.render();

    // Fleet Composition Pie Chart
    var fleetChart = new ApexCharts(document.querySelector("#fleetCompositionChart"), {
        chart: { type: 'pie', height: 320 },
        series: [45, 25, 20, 10],
        labels: ['Cars', 'Vans', 'Bikes', 'Buses'],
        colors: ['#1cc88a', '#36b9cc', '#f6c23e', '#e74a3b'],
        legend: { position: 'bottom' },
        tooltip: { y: { formatter: val => val + '%' } }
    });
    fleetChart.render();
});
</script>


